#!/bin/bash\n\n# ğŸŒ MOVE ANIME TO ACCESSIBLE STORAGE\n# Moves anime from Termux internal storage to accessible Downloads folder\n\nRED='\\033[0;31m'\nGREEN='\\033[0;32m'\nYELLOW='\\033[0;33m'\nBLUE='\\033[0;34m'\nPURPLE='\\033[0;35m'\nCYAN='\\033[0;36m'\nWHITE='\\033[0;37m'\nBOLD='\\033[1m'\nRESET='\\033[0m'\n\nclear\necho -e \"${BOLD}${PURPLE}ğŸŒ MOVE ANIME TO ACCESSIBLE STORAGE ğŸŒ${RESET}\"\necho -e \"${CYAN}â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•${RESET}\"\necho \"\"\n\n# Check source directory\nSOURCE_DIR=\"$HOME/anime/downloads\"\nif [ ! -d \"$SOURCE_DIR\" ]; then\n    echo -e \"${RED}âŒ No anime downloads found in Termux storage${RESET}\"\n    exit 1\nfi\n\n# Count anime files\nANIME_COUNT=$(find \"$SOURCE_DIR\" -type f \\( -name \"*.mp4\" -o -name \"*.mkv\" -o -name \"*.avi\" \\) 2>/dev/null | wc -l)\n\nif [ \"$ANIME_COUNT\" -eq 0 ]; then\n    echo -e \"${YELLOW}ğŸ“­ No anime files found to move${RESET}\"\n    exit 0\nfi\n\necho -e \"${GREEN}ğŸ“Š Found $ANIME_COUNT anime file(s) in Termux storage:${RESET}\"\necho \"\"\nfind \"$SOURCE_DIR\" -type f \\( -name \"*.mp4\" -o -name \"*.mkv\" -o -name \"*.avi\" \\) -exec basename {} \\; 2>/dev/null | nl\necho \"\"\n\n# Show options\necho -e \"${BOLD}${WHITE}Choose an option:${RESET}\"\necho -e \"${CYAN}[1]${RESET} ğŸ¬ Play video with termux-open (works from Termux storage)\"\necho -e \"${CYAN}[2]${RESET} ğŸ“ Open Termux file manager to browse files\"\necho -e \"${CYAN}[3]${RESET} ğŸ“‹ Show file paths for manual access\"\necho -e \"${CYAN}[4]${RESET} âŒ Exit\"\necho \"\"\nprintf \"${BOLD}Enter choice [1-4]: ${RESET}\"\nread -r choice\n\ncase $choice in\n    1)\n        echo -e \"${GREEN}ğŸ¬ Available videos:${RESET}\"\n        echo \"\"\n        \n        # List videos with numbers\n        mapfile -t videos < <(find \"$SOURCE_DIR\" -type f \\( -name \"*.mp4\" -o -name \"*.mkv\" -o -name \"*.avi\" \\) 2>/dev/null)\n        \n        for i in \"${!videos[@]}\"; do\n            echo -e \"${CYAN}[$((i+1))]${RESET} $(basename \"${videos[i]}\")\"\n        done\n        \n        echo \"\"\n        printf \"${BOLD}Select video to play [1-${#videos[@]}]: ${RESET}\"\n        read -r video_choice\n        \n        if [[ \"$video_choice\" =~ ^[0-9]+$ ]] && [ \"$video_choice\" -ge 1 ] && [ \"$video_choice\" -le \"${#videos[@]}\" ]; then\n            selected_video=\"${videos[$((video_choice-1))]}\"\n            echo -e \"${GREEN}ğŸ¬ Opening: $(basename \"$selected_video\")${RESET}\"\n            termux-open \"$selected_video\"\n        else\n            echo -e \"${RED}âŒ Invalid selection${RESET}\"\n        fi\n        ;;\n    2)\n        echo -e \"${GREEN}ğŸ“ Opening Termux file manager...${RESET}\"\n        if command -v termux-open &> /dev/null; then\n            termux-open \"$SOURCE_DIR\"\n            echo -e \"${GREEN}âœ… File manager opened to anime directory${RESET}\"\n        else\n            echo -e \"${RED}âŒ termux-open not available${RESET}\"\n            echo -e \"${CYAN}ğŸ’¡ Install termux-api: pkg install termux-api${RESET}\"\n        fi\n        ;;\n    3)\n        echo -e \"${GREEN}ğŸ“‹ File paths for manual access:${RESET}\"\n        echo \"\"\n        find \"$SOURCE_DIR\" -type f \\( -name \"*.mp4\" -o -name \"*.mkv\" -o -name \"*.avi\" \\) 2>/dev/null | while read -r file; do\n            echo -e \"${CYAN}ğŸ“${RESET} $file\"\n        done\n        echo \"\"\n        echo -e \"${YELLOW}ğŸ’¡ Use: termux-open \\\"path/to/file.mp4\\\" to play${RESET}\"\n        ;;\n    4)\n        echo -e \"${GREEN}ğŸ‘‹ Goodbye!${RESET}\"\n        exit 0\n        ;;\n    *)\n        echo -e \"${RED}âŒ Invalid choice${RESET}\"\n        exit 1\n        ;;\nesac\n\necho \"\"\necho -e \"${GREEN}âœ… Operation completed!${RESET}\"\necho -e \"${CYAN}ğŸ’¡ Run this script again to access your anime files${RESET}\"\n"